class Thread(QThread):
    def __init__(self, sensor_control_temp_value, sensor_control_temp):
        super().__init__()
        self.sensor_control_temp_value = sensor_control_temp_value
        self.sensor_control_temp = sensor_control_temp

    def run(self):
        while True:
            # if ConnectionException:
            #     self.sensor_control_temp_value.setText("0")
            #     self.sensor_control_temp.setText("Ошибка подключения к MOXA 1260")
            #     self.sensor_control_temp.setStyleSheet("background-color: red")
            #     print('error in if')
            try:
                temp = read_input_register_moxa_2() # запоминание данных температуры
                try: # при отключенной MOXA крашится программв, данная обработка ошибок ничего не несет в себе, но с ней не каршится
                    # так же добавлено перевод в int крашилось, при отсутсвии MOXA падает какой-то не известный тип данных и крашит программу
                    if int(temp) < 40:  # проверка на температуру во внутри канала
                        self.sensor_control_temp_value.setText("{}".format(temp))
                        self.sensor_control_temp.setStyleSheet("")
                        print(int(temp), datetime.datetime.now())
                    else:
                        self.sensor_control_temp_value.setText("{}".format(temp))
                        self.sensor_control_temp_value.setStyleSheet("background-color: red")
                        self.sensor_control_temp.setText("Внимание! Повышена температура")
                        self.sensor_control_temp.setStyleSheet("background-color: red")
                        print('error in else')
                except ValueError:  # вывод какого-то не понятного типа данных при отключенной moxa, ничего критичного тут нет, все работает
                    # при обычной работе этих ошибок не будет, на результат работы не влияет
                    pass
            except ConnectionException:  # Ничего не надо делать при этом исключении, была проверка на него в начале функции
                self.sensor_control_temp_value.setText("0")
                self.sensor_control_temp.setText("Ошибка подключения к MOXA 1260")
                self.sensor_control_temp.setStyleSheet("background-color: red")
                print('error in except')

            time.sleep(2)


